// Рабочее решение с использованием $http
const message = $input.first().json.message || 'Привет! Как дела?';

// Используем встроенный $http объект n8n
try {
  const response = await $http.post('https://llm.flyingplate.cloud/api/chat', {
    headers: {
      'Content-Type': 'application/json'
    },
    body: {
      messages: [
        {
          role: 'user',
          content: message
        }
      ]
    }
  });

  return [{
    json: {
      success: true,
      ai_response: response.body.response,
      tokens_used: response.body.usage?.total_tokens || 0,
      raw_response: response.body,
      timestamp: new Date().toISOString()
    }
  }];
} catch (error) {
  return [{
    json: {
      success: false,
      error: error.message,
      timestamp: new Date().toISOString()
    }
  }];
}
